// src/components/BookReader.js
import React, { useState } from 'react';

const BookReader = ({ book, chapters, onSaveProgress }) => {
    const [currentChapter, setCurrentChapter] = useState(0);
    const [page, setPage] = useState(1);

    const handleNextPage = () => setPage(page + 1);
    const handlePrevPage = () => setPage(page > 1 ? page - 1 : 1);

    return (
        <div className="w-full">
            <header className="flex justify-between items-center p-4 bg-gray-200">
                <h2 className="text-xl font-bold">{book.title}</h2>
                <button onClick={onSaveProgress} className="bg-blue-600 text-white px-4 py-2 rounded-md">
                    Save Progress
                </button>
            </header>

            <div className="flex">
                <nav className="w-1/4 p-4 bg-gray-100">
                    <h3 className="font-semibold text-lg">Table of Contents</h3>
                    <ul>
                        {chapters.map((chapter, index) => (
                            <li key={index} className="cursor-pointer" onClick={() => setCurrentChapter(index)}>
                                {chapter}
                            </li>
                        ))}
                    </ul>
                </nav>

                <div className="w-3/4 p-6">
                    <div className="text-right">
                        <button
                            onClick={handlePrevPage}
                            className="bg-gray-300 px-4 py-2 rounded-lg mr-4"
                            disabled={page === 1}
                        >
                            Previous
                        </button>
                        <button onClick={handleNextPage} className="bg-gray-300 px-4 py-2 rounded-lg">
                            Next
                        </button>
                    </div>
                    <div className="mt-8">
                        <h4 className="font-bold text-lg">Chapter {currentChapter + 1}</h4>
                        {/* Here is where the book content would be dynamically rendered */}
                        <p className="mt-4">Page {page} content...</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default BookReader;
