// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import BookSection from './components/BookSection';
import BookReader from './components/BookReader';

import './App.css';

// Example book data
const books = [
  { title: 'Practice of the Presence of God', cover: 'path-to-image1', author: 'Brother Lawrence' },
  // You can add more books here
];

function App() {
  return (
    <Router>
      <div className="app">
        <Sidebar />
        <div className="content">
          <h1>Home</h1>
          <BookSection title="The Collection" books={books} />
          <Switch>
            <Route path="/book/:bookTitle" component={BookReader} />
          </Switch>
        </div>
      </div>
    </Router>
  );
}

export default App;
