import Sidebar from './components/Sidebar';
import BookSection from './components/BookSection';
import './App.css';  // Add global styles for layout

function App() {
  return (
    <div className="app">
      <Sidebar />
      <div className="content">
        <h1 className="home-title">Home</h1> {/* Add class here */}

        {/* Add margin-top to the BookSection */}
        <BookSection title="The Collection" books={discountedBooks} />
      </div>
    </div>
  );
}

const discountedBooks = [
  { title: 'The Wild Robot', cover: 'path-to-image3' },
  { title: 'The Killer’s Daughter', cover: 'path-to-image4' },
  // Add more books
];

export default App;
