import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { auth, saveHighlight, removeHighlight, getHighlights } from '../services/firebase';

const BookReader = () => {
  const { bookTitle } = useParams();
  const [highlights, setHighlights] = useState([]);
  const [selectedText, setSelectedText] = useState('');
  const [error, setError] = useState('');

  // Load highlights when component mounts
  useEffect(() => {
    loadHighlights();
  }, [bookTitle]);

  const loadHighlights = async () => {
    try {
      const { highlights: savedHighlights, error } = await getHighlights(bookTitle);
      if (error) {
        setError(`Failed to load highlights: ${error}`);
        return;
      }
      setHighlights(savedHighlights);
      applyHighlights(savedHighlights);
    } catch (err) {
      setError('Failed to load highlights');
      console.error(err);
    }
  };

  const handleTextSelection = async () => {
    const selection = window.getSelection();
    const selectedText = selection.toString().trim();

    if (!selectedText) return;

    const range = selection.getRangeAt(0);
    const highlight = {
      text: selectedText,
      startPath: getNodePath(range.startContainer),
      startOffset: range.startOffset,
      endPath: getNodePath(range.endContainer),
      endOffset: range.endOffset,
      timestamp: new Date().toISOString()
    };

    try {
      const { error } = await saveHighlight(bookTitle, highlight);
      if (error) {
        setError(`Failed to save highlight: ${error}`);
        return;
      }
      setHighlights(prev => [...prev, highlight]);
      applyHighlight(highlight);
    } catch (err) {
      setError('Failed to save highlight');
      console.error(err);
    }
  };

  // Helper function to get DOM path to a node
  const getNodePath = (node) => {
    const path = [];
    let current = node;

    while (current.parentNode) {
      const index = Array.from(current.parentNode.childNodes).indexOf(current);
      path.unshift(index);
      current = current.parentNode;
    }

    return path;
  };

  // Helper function to get node from path
  const getNodeFromPath = (path) => {
    let current = document.body;

    for (const index of path) {
      current = current.childNodes[index];
    }

    return current;
  };

  // Apply a single highlight
  const applyHighlight = (highlight) => {
    try {
      const startNode = getNodeFromPath(highlight.startPath);
      const endNode = getNodeFromPath(highlight.endPath);

      const range = document.createRange();
      range.setStart(startNode, highlight.startOffset);
      range.setEnd(endNode, highlight.endOffset);

      const span = document.createElement('span');
      span.className = 'highlighted-text';
      span.dataset.timestamp = highlight.timestamp;
      range.surroundContents(span);
    } catch (err) {
      console.error('Failed to apply highlight:', err);
    }
  };

  // Apply all highlights
  const applyHighlights = (highlightsToApply) => {
    // Remove existing highlights first
    document.querySelectorAll('.highlighted-text').forEach(el => {
      const parent = el.parentNode;
      while (el.firstChild) parent.insertBefore(el.firstChild, el);
      parent.removeChild(el);
    });

    // Apply new highlights
    highlightsToApply.forEach(highlight => applyHighlight(highlight));
  };

  // Handle removing a highlight
  const handleRemoveHighlight = async (event) => {
    if (!event.target.classList.contains('highlighted-text')) return;

    const timestamp = event.target.dataset.timestamp;
    const highlightToRemove = highlights.find(h => h.timestamp === timestamp);

    if (!highlightToRemove) return;

    try {
      const { error } = await removeHighlight(bookTitle, highlightToRemove);
      if (error) {
        setError(`Failed to remove highlight: ${error}`);
        return;
      }
      setHighlights(prev => prev.filter(h => h.timestamp !== timestamp));

      // Remove highlight span
      const parent = event.target.parentNode;
      while (event.target.firstChild) {
        parent.insertBefore(event.target.firstChild, event.target);
      }
      parent.removeChild(event.target);
    } catch (err) {
      setError('Failed to remove highlight');
      console.error(err);
    }
  };
};

export default BookReader;
