import React, { useState, useEffect } from 'react';
import { db } from '../services/firebaseConfig';  // Import 'db'

function BookLibrary({ onSelectBook }) {
    const [books, setBooks] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        const fetchBooks = async () => {
            const booksCollection = await db.collection('books').get();  // Use 'db'
            setBooks(booksCollection.docs.map(doc => ({ id: doc.id, ...doc.data() })));
        };
        fetchBooks();
    }, []);

    return (
        <div className="library">
            <input
                type="text"
                placeholder="Search for a book"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-bar"
            />
            <div className="book-grid">
                {books
                    .filter(book => book.title.toLowerCase().includes(searchTerm.toLowerCase()))
                    .map((book) => (
                        <div key={book.id} className="book-card" onClick={() => onSelectBook(book)}>
                            <img src={book.coverImage} alt={book.title} className="book-cover" />
                            <p>{book.title}</p>
                        </div>
                    ))}
            </div>
        </div>
    );
}

export default BookLibrary;
