// src/services/firebase.js
import { initializeApp } from "firebase/app";
import { getFirestore, doc, setDoc } from "firebase/firestore";

// Firebase configuration (use your actual config here)
const firebaseConfig = {
    apiKey: "YOUR_API_KEY",
    authDomain: "YOUR_AUTH_DOMAIN",
    projectId: "YOUR_PROJECT_ID",
    storageBucket: "YOUR_STORAGE_BUCKET",
    messagingSenderId: "YOUR_MESSAGING_SENDER_ID",
    appId: "YOUR_APP_ID",
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

// Initialize Firestore
const db = getFirestore(app);

// Define and export the `saveBookProgress` function
export const saveBookProgress = async (userId, bookId, chapter, page) => {
    try {
        await setDoc(doc(db, "users", userId, "savedBooks", bookId), {
            chapter,
            page,
        });
        console.log("Book progress saved successfully.");
    } catch (error) {
        console.error("Error saving book progress:", error);
    }
};

export { db };
