import React, { useState } from 'react';
import BookLibrary from './components/BookLibrary';
import BookReader from './components/BookReader';

const App = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const selectBook = (book) => {
    setSelectedBook(book);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center">
      {/* Header */}
      <header className="bg-blue-600 w-full p-4 shadow-lg">
        <h1 className="text-white text-3xl text-center">AI Christian Texts</h1>
      </header>

      {/* Search Bar */}
      <div className="mt-6">
        <input
          type="text"
          className="border p-2 rounded w-full md:w-96"
          placeholder="Search for a book..."
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>

      {/* Main Content */}
      <div className="container mx-auto mt-6 flex flex-col lg:flex-row gap-6">
        {/* Book Library */}
        {!selectedBook && (
          <BookLibrary searchTerm={searchTerm} selectBook={selectBook} />
        )}

        {/* Book Reader */}
        {selectedBook && (
          <BookReader book={selectedBook} goBack={() => setSelectedBook(null)} />
        )}
      </div>
    </div>
  );
};

export default App;
