// src/components/BookSection.js
import React from 'react';
import './BookSection.css';

const BookSection = ({ title, books }) => {
    const handleBookClick = (url) => {
        window.open(url, '_blank'); // Open the book in a new tab
    };

    return (
        <div className="book-section">
            <h2 className="collection-title">{title}</h2>
            <div className="book-list">
                {books.map((book, index) => (
                    <div className="book-item" key={index}>
                        {book.cover && <img src={book.cover} alt={book.title} />}
                        <p
                            className="book-title"
                            onClick={() => handleBookClick(book.link)} // Handle click on book title
                            style={{ cursor: 'pointer', color: '#00f', textDecoration: 'underline' }} // Optional styles for the title
                        >
                            {book.title}
                        </p>
                        <p className="book-author">by {book.author}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default BookSection;
