import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { auth, saveHighlight, getHighlights } from '../services/firebase';

const BookReader = ({ books }) => {
  const { bookTitle } = useParams();
  const [currentBook, setCurrentBook] = useState(null);
  const [highlights, setHighlights] = useState([]);
  const [error, setError] = useState('');

  // Find the current book when bookTitle changes
  useEffect(() => {
    const book = books.find(b => b.title === bookTitle);
    if (book) {
      setCurrentBook(book);
    } else {
      setError('Book not found');
    }
  }, [bookTitle, books]);

  // Load saved highlights when book changes
  useEffect(() => {
    if (currentBook) {
      loadHighlights();
    }
  }, [currentBook]);

  const loadHighlights = async () => {
    try {
      const { highlights: savedHighlights, error } = await getHighlights(currentBook.title);
      if (error) {
        console.error('Error loading highlights:', error);
        return;
      }
      setHighlights(savedHighlights || []);
    } catch (err) {
      console.error('Failed to load highlights:', err);
    }
  };

  // Monitor highlighting in the iframe
  useEffect(() => {
    const handleMessage = async (event) => {
      // We'll need to verify the origin matches CCEL's domain
      if (event.origin !== "https://ccel.org") return;

      // Assuming CCEL sends highlight data in a specific format
      if (event.data.type === 'highlight') {
        const highlightData = {
          text: event.data.text,
          location: event.data.location, // This might be page number, paragraph, etc.
          timestamp: new Date().toISOString(),
          bookTitle: currentBook.title
        };

        try {
          const { error } = await saveHighlight(currentBook.title, highlightData);
          if (error) {
            console.error('Error saving highlight:', error);
            return;
          }
          setHighlights(prev => [...prev, highlightData]);
        } catch (err) {
          console.error('Failed to save highlight:', err);
        }
      }
    };

    window.addEventListener('message', handleMessage);
    return () => window.removeEventListener('message', handleMessage);
  }, [currentBook]);

  if (!currentBook) {
    return <div className="book-reader">Loading...</div>;
  }

  return (
    <div className="book-reader">
      {error && <div className="error-message">{error}</div>}
      <h1>{currentBook.title}</h1>
      <iframe
        src={currentBook.link}
        style={styles.iframe}
        title={currentBook.title}
        sandbox="allow-same-origin allow-scripts"
      />
    </div>
  );
};

const styles = {
  iframe: {
    width: '100%',
    height: 'calc(100vh - 120px)', // Adjust based on your layout
    border: 'none',
    backgroundColor: '#fff',
    borderRadius: '8px',
    marginTop: '20px',
  }
};

export default BookReader;
