import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import BookSearch from './components/BookSearch';
import Library from './components/Library';
import BookReader from './components/BookReader';
import { saveBookProgress } from './services/firebase';

const booksData = [
  { id: 1, title: "The Confessions of St. Augustine", author: "Augustine", cover: "/covers/augustine.jpg", chapters: ["Chapter 1", "Chapter 2"] },
  { id: 2, title: "The Imitation of Christ", author: "Thomas à Kempis", cover: "/covers/kempis.jpg", chapters: ["Chapter 1", "Chapter 2"] },
  // More books here
];

const App = () => {
  const [books, setBooks] = useState(booksData);
  const [selectedBook, setSelectedBook] = useState(null);
  const [chapter, setChapter] = useState(1);
  const [page, setPage] = useState(1);

  const handleSearch = (query) => {
    const filteredBooks = booksData.filter((book) => book.title.toLowerCase().includes(query.toLowerCase()));
    setBooks(filteredBooks);
  };

  const handleSelectBook = (book) => {
    setSelectedBook(book);
    setChapter(1); // Reset to first chapter
    setPage(1); // Reset to first page
  };

  const handleSaveProgress = () => {
    saveBookProgress("userId", selectedBook.id, chapter, page); // Save current chapter and page
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route
            path="/"
            element={
              <>
                <BookSearch onSearch={handleSearch} />
                <Library books={books} onSelectBook={handleSelectBook} />
              </>
            }
          />
          <Route
            path="/read/:bookId"
            element={
              <BookReader
                book={selectedBook}
                chapters={selectedBook ? selectedBook.chapters : []}
                onSaveProgress={handleSaveProgress}
                chapter={chapter}
                setChapter={setChapter}
                page={page}
                setPage={setPage}
              />
            }
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
