// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import BookSearch from './components/BookSearch';
import Library from './components/Library';
import BookReader from './components/BookReader';
import { saveBookProgress } from './services/firebase';

const booksData = [
  { id: 1, title: "The Confessions of St. Augustine", author: "Augustine", cover: "/covers/augustine.jpg" },
  { id: 2, title: "The Imitation of Christ", author: "Thomas à Kempis", cover: "/covers/kempis.jpg" },
  // More books here
];

const App = () => {
  const [books, setBooks] = useState(booksData);
  const [selectedBook, setSelectedBook] = useState(null);

  const handleSearch = (query) => {
    const filteredBooks = booksData.filter((book) => book.title.toLowerCase().includes(query.toLowerCase()));
    setBooks(filteredBooks);
  };

  const handleSelectBook = (book) => setSelectedBook(book);

  const handleSaveProgress = () => {
    saveBookProgress("userId", selectedBook.id, 0, 1); // Save progress (chapter 0, page 1 as an example)
  };

  return (
    <Router>
      <div className="App">
        {!selectedBook ? (
          <>
            <BookSearch onSearch={handleSearch} />
            <Library books={books} onSelectBook={handleSelectBook} />
          </>
        ) : (
          <BookReader book={selectedBook} chapters={["Chapter 1", "Chapter 2"]} onSaveProgress={handleSaveProgress} />
        )}
      </div>
    </Router>
  );
};

export default App;
