import React, { useState, useEffect } from 'react';
import { db } from '../services/firebaseConfig';  // Import 'db'
import ChapterNavigation from './ChapterNavigation';

function BookReader({ selectedBook }) {
    const [chapter, setChapter] = useState(null);
    const [currentChapterIndex, setCurrentChapterIndex] = useState(0);

    useEffect(() => {
        if (selectedBook) {
            const fetchChapter = async () => {
                const chapterDoc = await db
                    .collection('books')
                    .doc(selectedBook.id)
                    .collection('chapters')
                    .doc(`chapter${currentChapterIndex}`)
                    .get();  // Use 'db'

                setChapter(chapterDoc.data());
            };
            fetchChapter();
        }
    }, [selectedBook, currentChapterIndex]);

    return (
        <div className="book-reader">
            {chapter ? (
                <>
                    <h2>{chapter.title}</h2>
                    <p>{chapter.content}</p>
                    <ChapterNavigation
                        currentChapterIndex={currentChapterIndex}
                        setCurrentChapterIndex={setCurrentChapterIndex}
                        totalChapters={selectedBook.totalChapters}
                    />
                </>
            ) : (
                <p>Loading chapter...</p>
            )}
        </div>
    );
}

export default BookReader;
